//import { Router, Routes } from 'react-router-dom'
import NavTodos from './components/NavTodos';
import TodoForm from './components/TodoForm';
import { useState } from 'react';
import TodoItem from './components/TodoItem';
import { useNavigate } from 'react-router-dom';

function App() {

  const navigate = useNavigate()

  const [todos, setTodos] = useState([{
    text,
    isCompleted: false
  }])

  const addTodo = (text) => {
  const newAddedTodo = [...todos, { text }]
  setTodos(newAddedTodo)
}

  const removeTodo = index => {
    const removedTodos = [...todos]
    removedTodos.splice(index, 1)
    setTodos(removedTodos)
    navigate('/deletedTodos')
  }

  const editTodo = index => {
    const editedTodo = todos.filter(todo => todo.index === index)
    setTodos(editedTodo)
  }

  return (
    <>
    <NavTodos/>
    <TodoForm addTodo={addTodo}/>
    <div className='flex justify-center items-center'>

      {todos.map((todo, index) => (
        <TodoItem 
        key={index} 
        index={index} 
        addTodo={todo} 
        removeTodo={removeTodo}
        editTodo={editTodo}
        />
      ))}
    </div>
    </>
  );
}

export default App;
